-- Define available catalogsCREATECATALOG MyCatalog  WITH (    'type' = 'hive'  );USECATALOG MyCatalog;-- Define available databaseCREATEDATABASE MyDatabase;USEMyDatabase;-- Define TABLECREATE TABLE employee_information(    emp_id  INT,    name    VARCHAR,    dept_id INT) WITH (      'connector' = 'filesystem',      'path' = '/opt/flink/data/employee.csv',      'format' = 'csv'      );-- Define VIEWCREATE VIEW NameView ASSELECT nameFROM employee_information;-- Define user-defined functions here.CREATE FUNCTION myUDF AS 'com.alibaba.blink.udx.RdsMonitorConvert';-- Properties that change the fundamental execution behavior of a table program.SET'execution.runtime-mode' = 'streaming'; -- execution mode either 'batch' or 'streaming'SET'sql-client.execution.result-mode' = 'table'; -- available values: 'table', 'changelog' and 'tableau'SET'sql-client.execution.max-table-result.rows' = '10000'; -- optional: maximum number of maintained rowsSET'parallelism.default' = '1'; -- optional: Flink's parallelism (1 by default)SET'pipeline.auto-watermark-interval' = '200'; --optional: interval for periodic watermarksSET'pipeline.max-parallelism' = '10'; -- optional: Flink's maximum parallelismSET'table.exec.state.ttl' = '1000'; -- optional: table program's idle state timeSET'restart-strategy.type' = 'fixed-delay';-- Configuration options for adjusting and tuning table programs.SET'table.optimizer.join-reorder-enabled' = 'true';SET'table.exec.spill-compression.enabled' = 'true';SET'table.exec.spill-compression.block-size' = '128kb';